(function() {
    window.ActivityFeed = (function() {

        ActivityFeed.prototype.toggle                   = null;
        ActivityFeed.prototype.toggle_icon_show         = null;
        ActivityFeed.prototype.toggle_icon_hide         = null;
        ActivityFeed.prototype.list                     = null;
        ActivityFeed.prototype.list_container           = null;
        ActivityFeed.prototype.container                = null;
        ActivityFeed.prototype.project_name             = null;
        ActivityFeed.prototype.piles                    = null;
        ActivityFeed.prototype.user_id                  = null;

        function ActivityFeed(options) { 
            // copy parameters
            _.extend(this, options);
            this.list.data('_user_id', options.user_id);

            var that = this;

            // setup toggling
            this.toggle_icon_show.hide();
            this.toggle_icon_hide.show();

            this.toggle.click(function() {
                var is_visible = $(this).data('is-visible');
                if (is_visible || is_visible == 'true') {
                    that.hide();
                    is_visible = false;
                } else {
                    that.show();
                    is_visible = true;
                }
                // todo: refactor
                $(this).data('is-visible', is_visible);
            });

            this._connect_activity_links();

            // setup activity paginator:
            this.list.jscroll({ 
                nextSelector: '.activity_paginator',
                callback: this._connect_activity_links,
                loadingHtml: '<p class="activity_loading">Loading...<img src="/assets/loading.gif" height="24px" width="24px" style="float: right;"></img></p>'
            });
        }

        ActivityFeed.prototype._connect_activity_links = function() {
            var user_id = $('#activity_list').data('_user_id');

            $("a.activity_card").each(function(i, elem) {
                // TODO: Refactor
                var obj = $(elem);
                cc_connect_card_to_modal(obj.text(), this.project_name, 
                                         obj, true);
            });



            $(".activity_username").click(function(e) {
                e.preventDefault();
                var url = $(this).attr("href");
                var tokens = url.split("/");
                
                if (tokens[tokens.length - 1] == user_id) {
                    show_profile_dialog(url);
                } else {
                    show_other_profile_dialog(url);
                }
                return false;
            });

        };

        ActivityFeed.prototype.reload = function() {
            console.log("BEGIN RELOAD");
            var url = "/p/" + this.project_name + "/activity";
            var that = this;
            $.get(url, function(data) {
                that.list.html(data);
                that.list.animate({scrollTop: 0}, "fast");
                that._connect_activity_links();
                console.log("END RELOAD");
            });
        };

        ActivityFeed.prototype.show = function() {
            this.toggle_icon_show.hide();
            this.toggle_icon_hide.show();
            this.list_container.show();

            $("#piles").width("84%");
            $("#activity_container").width("16%");
            $("#activity_toggle").width("7%");
        };

        ActivityFeed.prototype.hide = function() {
            this.toggle_icon_show.show();
            this.toggle_icon_hide.hide();
            this.list_container.hide();

            $("#piles").width("99%");
            $("#activity_container").width("1%");
            $("#activity_toggle").width("100%");
        };

        return ActivityFeed;
    })();

}).call(this);

/**
 * Prototype for CardAttachments object. This allows the user
 * to upload a file directly via Amazon S3 and then attach it to
 * a card. Attachments are then made visible in the card
 * automatically via the save_and_reload() method.
 */
(function() {
    window.CardAttachments = (function() {

        CardAttachments.prototype.project_name      = null;
        CardAttachments.prototype.card_id           = null;
        CardAttachments.prototype.file_input        = null; 
        CardAttachments.prototype.file_input_button = null;
        CardAttachments.prototype.progress_selector = null;
        CardAttachments.prototype.progress          = null;
        CardAttachments.prototype.progress_bar      = null;
        CardAttachments.prototype.progress_text     = null;
        CardAttachments.prototype.attachments_selector  = null;

        function CardAttachments(options) { 
            // copy parameters
            _.extend(this, options);

            // Reference external scope.
            var that = this;

            // find progress element references
            this.progress_bar   = require(this.progress.find(".progress-bar"));
            this.progress_text  = require(this.progress.find(".progress-text"));

            // Hide the real file input, but make sure it can still be clicked
            // in all browsers. hide() is out of the question.
//            var wrapper = jQuery('<div/>').css({height:0,width:0,'overflow':'hidden'});
//            var real_file_input = jQuery(this.file_input).wrap(wrapper);
            this.file_input.hide();

            // Connect the file_input_button to click the real file
            // input:
            jQuery(this.file_input_button).click(function() {
                that.file_input.click();
            });

            // register change listener for when user selects file.
            jQuery(this.file_input).change(function() {
                var filename = that.get_filename();
                that.s3_upload(filename);
            });
        }


        CardAttachments.prototype.s3_upload = function(filename) {
            var that = this;

            this.progress.fadeIn();

            var s3upload = new S3Upload({
                file_dom_selector: this.file_input,
                s3_sign_put_url: '/sign_s3_upload/',

                onProgress: function(percent, message) { 
                    that.progress_bar.width(percent + "%");
                    var msg = "Uploading: " + filename + " " + percent + "%";
                    that.progress_text.text(msg);
                },

                onFinishS3Put: function(url) { 
                    console.log("onFinishS3Put: finish: ", url);
                    that.save_and_reload(url, filename);
                    that.progress_bar.width(0);
                },

                onError: function(message) {
                    alert(message);
                    alert("Error uploading file. Please try again.");
                    that.file_input.val("");
                    that.progress_bar.width(0);
                }

            }, filename);
        };


        CardAttachments.prototype.get_filename = function(file_input) {
            var fullPath = this.file_input.val();

            if (fullPath) {
                var startIndex = (fullPath.indexOf('\\') >= 0 ? 
                    fullPath.lastIndexOf('\\') : fullPath.lastIndexOf('/'));

                var filename = fullPath.substring(startIndex);

                if (filename.indexOf('\\') === 0 || filename.indexOf('/') === 0) {
                    filename = filename.substring(1);
                }

                return filename;
            }

            return "unknown_filename";
        };


        CardAttachments.prototype.save_and_reload = function(url, filename) {

            var api_url = "cards/" + this.card_id + "/attachments";

            var data = JSON.stringify({url: url, filename: filename});

            var that = this;
            $.ajax({
                type: "POST",
                url: api_url,
                data: data, 
                success: function(data) {
                    console.log("fade");
                    that.progress.fadeOut("slow", function() {
                        console.log("replaceWith");
                        console.log(data);
                        jQuery(that.attachments_selector).replaceWith(data);
                    });

                  var selector = "#card_" + that.card_id + "_attachment_count";
                  $(selector).text(Number($(selector).text()) + 1);
                },
                failure: function(data) {
                    alert("Failed to refresh attachments. Please reload the page.");
                },
                dataType: "html",
                contentType: "application/json;charset=UTF-8"
            });
        };

        return CardAttachments;
    })();

}).call(this);

